  // roomName이 같을 경우 처리
  const [roomName, setRoomName] = useState("");
  const [roomList, setRoomList] = useState({});

  const createRoom = () => {

      if(roomName == "") {
        alert("Please Input the room name");

      } else {
          Axios.post('http://localhost:3001/createRoom', {
            roomname : roomName,
          }).then(response => {
            console.log(response.data);
          });
      }

      Axios.post('http://localhost:3001/roomList', {

      }).then(respsonse => {
        
        setRoomList(respsonse.data);
        console.log(roomList);
      });
  }

  return (
    <div>
      
      <div className='roomOpen'>
        <input type="text" className='roomName' placeholder='Create Room' onChange={(e) => {setRoomName(e.target.value)}}/>
        <button onClick={createRoom}>Create</button>
      </div>
      
    </div>
  )
}

export default Main
