import React, { useContext, useState } from 'react'
import { LoginContext } from '../Contexts/LoginContext'
import Axios from 'axios';
import { Link } from 'react-router-dom';

function Login() {

    const {setUsername} = useContext(LoginContext);

    const [logusername, setLogusername] = useState("");
    const [logpassword, setLogpassword] = useState("");
    const [notice, setNotice] = useState("");

    const login = () => {

        if(logusername == "") {
            setNotice("Please Enter Username");
        } else if (logpassword == "") {
            setNotice("Please Enter Password");
        } else {
            Axios.post('http://localhost:3001/login', {
                username : logusername,
                password : logpassword,
            }).then(response => {
                console.log(response.data);

                if(response.data.message) {
                    setNotice(response.data.message);
                    // alert(response.data.message);
                    // LoginForm CSS 추가 후 위 문구가 나오도록 수정
                } else {
                    setUsername(logusername);
                }
            });
        }  
    }

  return (
    <div className="login">
        <h1>Login</h1>
        <input type="text" placeholder="Username..."  onChange={(e) => {setLogusername(e.target.value)}}/>
        <input type="password" placeholder="Password..." onChange={(e) => {setLogpassword(e.target.value)}}/>
        <button onClick={login}>Login</button>
        <Link to="/signIn">
            <a>Create a New Accout ?</a>
        </Link>
        <span>{notice}</span>
      </div>
  )
}

export default Login
